mapper.select:

select c.content, i.itemtitle, i.itemcost, i.itemwish, i.itemimg1, i.itemview, i.itemno
from common c, member m, (
        SELECT rownum rn ,  a.*
        FROM  (select * from item order by itemdate desc) a
     ) i
where c.mcd = m.locno
and i.id = m.id
And   rn >= #{start}
AND   rn <= #{end}
